delimiter ;;
CREATE PROCEDURE `insert_AgentTransfer_Order`(IN `transfer_id_` varchar(64),  IN `proxy_guid` int,IN `player_guid` int, IN `transfer_type` int, IN `transfer_money` int,IN `platform_id_` varchar(256),IN `channel_id_` varchar(255), IN `promoter_id_` int)
  COMMENT '代理上转账订单金币流水'
BEGIN
	DECLARE ret_code INT DEFAULT 0;
	DECLARE order_count INT DEFAULT 0;

	SELECT count(1) INTO order_count FROM t_Agent_recharge_order WHERE transfer_id = transfer_id_;

	IF order_count = 0 THEN
		INSERT INTO t_Agent_recharge_order (`transfer_id`,`proxy_guid`,`player_guid`,`transfer_type`,`transfer_money`,`platform_id`,`channel_id`,`promoter_id`) VALUES (transfer_id_,proxy_guid,player_guid,transfer_type,transfer_money,platform_id_,channel_id_,promoter_id_);

		IF ROW_COUNT() > 0 THEN
			SELECT 1 INTO ret_code;
		ELSE
			SELECT 0 INTO ret_code;
		END IF;
		
	ELSE
		SELECT 2 INTO ret_code;
	END IF;
	
	select ret_code as ret;

END
;;
